#!/usr/bin/env python3

import sys

# Define variables
user_seq = ''




# Replace cut sites with ^
marked_seq = user_seq.replace('GAATTC', 'G^AATTC')

# split by ^
frags = marked_seq.split('^')

# sort frags by length
frags.sort(key=len)

for frag in frags:
    frag_len = len(frag)
    pos = user_seq.find(frag) + 1
    end = pos + frag_len - 1

    print(frag_len, pos, end, sep='\t')


    


