#!/usr/bin/env python3

import sys

my_seq = ''
#my_seq = 'ATCG'

my_list = list(my_seq)
my_comp = {}
for n in my_list:
    my_comp[n] = my_seq.count(n)

for base in my_comp:
    print(f'{base} count is {my_comp[base]}')

print(f'The GC content is: {(my_comp['G'] + my_comp['C']) / len(my_seq):.2%}')



